mapper.select:
		select * from(
    		select /*+INDEX_DESC(mc_notice nno) */
        	rownum rn, nno, ntitle, ncontent, nregdate, ncount
        	from mc_notice)
        where rn between #{startRow} and #{endRow}

mapper.update:
update mc_notice set
ntitle = #{ntitle},
ncontent = #{ncontent}
where nno = #{nno}

update mc_notice set
ncount = ncount+1
where nno = #{nno}
